import React, { useState, useEffect } from 'react';
import { axiosWithAuth } from '../utils/axiosWithAuth.js'

import Friend from './Friend.js';

const Friends = props =>{

    const [friends, setFriends] = useState([])
    const [addFriend, setAddFriend] = useState({id:`${friends.length+1}`, name:'', age:'', email:''})

    useEffect(()=>{
        axiosWithAuth()
        .get('/api/friends')
        .then(res => setFriends(res.data))
        .catch(err => console.log({ err }));
    },[])

    const handleChange = event => {
        setAddFriend({ ...addFriend, [event.target.name]: event.target.value })
        console.log(addFriend)
    };

    console.log(friends)

    const makeFriendsCards = ()=>{
       return friends.map(element => <Friend friend={element} key={element.id}/>)
    }

    return(
        <div className='friends-container'>
            {makeFriendsCards()}
            <form className='form' >
                <label htmlFor="name">
                    Name:
                <input name='name' type="text" onChange={handleChange} />
                </label>
                <label htmlFor="age">
                    Age:
                <input name='age' type="text" onChange={handleChange} />
                </label>
                <label htmlFor="name">
                    Email:
                <input name='email' type="text" onChange={handleChange} />
                </label>
                <input className='submit' name='submit' type='submit' />

            </form>
        </div>
    )
}

export default Friends;
