import React, { useState, useEffect } from 'react';
import { axiosWithAuth } from '../utils/axiosWithAuth.js'

import Friend from './Friend.js';

const Friends = props =>{
    //state for friends, get data from API
    const [friends, setFriends] = useState([])
    const [id, setId] = useState()
    console.log(id)
    const [addFriend, setAddFriend] = useState({id:id, name:'', age:'', email:''})
    //get data on render
    useEffect(()=>{
        axiosWithAuth()
        .get('/api/friends')
        .then(res => {
            setFriends(res.data)
            setId(friends.length+1)
        })
        .catch(err => console.log({ err }));
    },[])
    //state for form to add a friend


    const handleChange = event => {
        setAddFriend({ ...addFriend, [event.target.name]: event.target.value })
    };



    console.log(friends)

    const makeFriendsCards = ()=>{
       return friends.map(element => <Friend friend={element} key={element.id}/>)
    }

    const submitFriend = event =>{
        event.preventDefault()
        axiosWithAuth()
        .post('/api/friends', addFriend)
        .then(res => {
            console.log(res)
            window.location.reload()
        })
        .catch(err => console.log(err))
    }

    return(
        <div className='friends-container'>
            {makeFriendsCards()}
            <h3>Add a Friend</h3>
            <form className='form' onSubmit={submitFriend}>
                <label htmlFor="name">
                    Name:
                <input name='name' type="text" onChange={handleChange} />
                </label>
                <label htmlFor="age">
                    Age:
                <input name='age' type="text" onChange={handleChange} />
                </label>
                <label htmlFor="name">
                    Email:
                <input name='email' type="email" onChange={handleChange} />
                </label>
                <input className='submit-friend-button' name='submit' type='submit' />

            </form>
        </div>
    )
}

export default Friends;
